import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import UsersController from 'App/Controllers/Http/UsersController'

export default class General {
  public async handle(ctx: HttpContextContract, next: () => Promise<void>) {
    const authorizationHeader = ctx.request.header('Authorization')
    if(authorizationHeader == undefined) {
      return  ctx.response.status(400).send({
        mensaje: 'Falta el token de authorización',
        estado: 401,
      })
    }
    const token = authorizationHeader

    try {
      UsersController.verificarToken(token)
      await next()
    } catch (error) {
      ctx.response.status(400).send('Token incorrecto')
    }
  }
}

import type { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import UsersController from 'App/Controllers/Http/UsersController'
import User from 'App/Models/User'

export default class Admin {
  public async handle(ctx: HttpContextContract, next: () => Promise<void>) {
    const authorizationHeader:any = ctx.request.header('authorization')

    try {
      const {id, role} = await UsersController.obtenerPayload(authorizationHeader)
      const user = await User.find(id)
      if (!user){
        return ctx.response.status(401).json({
          'msg': 'Token no valido'})
      }
      if(role !== 1){
        return ctx.response.status(401).json({
          'msg': 'No tiene autorización para está acción'})
      }

      await next()
    } catch (error) {
      return ctx.response.status(400).json({
        'msg': 'Token no valido'})
    }
  }
}

